JS
const fs = require('fs')
const {
  LineItemStateCsvParser,
  AddReturnInfoCsvParser,
  DeliveriesCsvParser,
} = require('@commercetools/csv-parser-orders')

const parser = new LineItemStateCsvParser({
  logger: {
    error: console.error,
    warn: console.warn,
    info: console.log,
    verbose: console.log,
  },
  csvConfig: {
    delimiter: ',',
